mapper.select:
SELECT  rpl_pass		AS rplPass

  FROM  default_rpl
 WHERE  reply_id = #{replyId}

mapper.insert:
INSERT INTO default_rpl(

		reply_id
	  ,	content
	  , writer
	  , rpl_pass
	  , reg_dt
	  , up_dt
	  , rpl_use
	  , bbs_id
	  , rpl_group
	  , grade

	  )VALUES(

	  	#{replyId}
	  , #{content}
	  , #{writer}
	  , #{rplPass}
	  , NOW()
	  , NOW()
	  , 'Y'
	  , #{bbsId}
	  , #{replyId}
	  , 0

	  )ON DUPLICATE KEY UPDATE
	  content = #{content}
	  , writer = #{writer}
	  , rpl_pass = #{rplPass}
	  , up_dt = NOW()

mapper.update:
UPDATE	default_rpl
   SET	rpl_use = 'N'
 WHERE	reply_id = #{replyId}
