mapper.insert:
INSERT INTO default_bbs(

		bbs_id
	  , title
	  , content
	  , read_cnt
	  , regist_nm
	  , regist_dt
	  , updt_nm
	  , updt_dt
	  , use_at

	  )VALUES(
	    #{bbsId}
	  , #{title}
	  , #{content}
	  , 0
	  , #{registNm}
	  , NOW()
	  , #{registNm}
	  , NOW()
	  , 'Y'
	  )

mapper.update:
UPDATE default_bbs
   SET title = #{title}
     , content = #{content}
     , updt_nm = #{updtNm}
     , updt_dt = NOW()
 WHERE bbs_id = #{bbsId}

UPDATE default_bbs
   SET use_at = 'N'
 WHERE bbs_id = #{bbsId}

UPDATE  default_bbs
   SET  read_cnt = read_cnt + 1
 WHERE  bbs_id = #{bbsId}
